import React from 'react';
import {getRanking} from '../API/API_Ranking.js'




class Ranking extends React.Component {

    //Constructeur : intialisation de chaque paramètre du state utilisés
      constructor(props) {
          super(props)
          this.state = {
            teamsWestern: [],
            teamsEastern: []
          }
        }

        //Mise à jour du state à la construction du component, avec les données collectés de l'api donnant la liste des joueurs
        componentDidMount()
        {
            this._loadTeams()
        }

        _loadTeams() {
          getRanking().then(data => {
            console.log(data.conferences[0].divisions.teams)
            this.setState({
              teamsWestern: [ ...this.state.teamsWestern, ...Object.entries(data.conferences[0].divisions) ],
              //teamsWestern: data.conferences[0].divisions.teams 
            })
            //console.log(teamsWestern)

          })
        }


      render() {
          return (
            <div>
                <h2>ATP Ranking</h2>
                <p>Coin coin je t'aime</p>
            </div>
          )
      }
  }

  export default Ranking
