import React, { Component } from 'react';
import { Carousel } from 'react-bootstrap';
import '../cssFiles/home.css';
import {getMatchResults} from '../API/API_MatchResults'

export default class MatchResults extends Component {

  //Constructeur : intialisation de chaque paramètre du state utilisés
  constructor(props) {
    super(props)
    this.state = {
      loading: false,
      items: null,
    }
  }

  componentDidMount () {

    getMatchResults().then(data => {
      console.log(data)
      this.setState({
        loading: true,
        items: data,
        })
      })

    }

    render() {
      var {loading, items} = this.state;

      if(!loading)
      {
        return <div>Loading...</div>;
      }
      else{

        return (
          <div>
            Home team : {items.data[0].home_team.city} / Score : {items.data[0].home_team_score}
          </div>
        );
      }

      }
    
  }
