import React, { Component } from 'react';
import { Table } from 'react-bootstrap';
import '../cssFiles/home.css';
import {getMatchResults} from '../API/API_MatchResults'

export default class MatchResults extends Component {
  //Constructeur : intialisation de chaque paramètre du state utilisés
  constructor(props) {
    super(props);
    var today = new Date();
    this.state = {
      loading: false,
      items: null,
      currentYear: today.getUTCFullYear(),
      currentMonth: today.getUTCMonth() + 1,
      currentDay: today.getDate(),
      tableauVar : 278
    }
  }

  componentDidMount () {

    getMatchResults().then(data => {
      console.log(data)
      this.setState({
        loading: true,
        items: data,
        })
      })

    }

    render() {
      var {loading, items} = this.state;

      if(!loading)
      {
        return <div>Loading...</div>;
      }
      else{

        return (
          <Table striped bordered hover>
            <thead>
              <tr>
                <th>Logo Home Team</th>
                <th>Home Team</th>
                <th>Score</th>
                <th>Away Team</th>
                <th>Logo Away Team</th>
              </tr>
            </thead>
            {items.games.slice(260, 265).map(item => (
            <tbody key={item.id}>
              <tr>
                <td>1</td>
                <td>{item.home.name}</td>
                <td>Otto</td>
                <td>@mdo</td>
                <td>hihi</td>
              </tr>
            </tbody>
            ))}
          </Table>
        );
      }

      }
    
  }
