import React, { Component } from 'react';
import { Table } from 'react-bootstrap';
import '../cssFiles/home.css';
import {getMatchResults} from '../API/API_MatchResults'
import { imagesList } from '../images'

export default class MatchResults extends Component {
  //Constructeur : intialisation de chaque paramètre du state utilisés
  constructor(props) {
    super(props);
    var today = new Date();
    this.state = {
      loading: false,
      items: null,
      currentYear: today.getUTCFullYear(),
      currentMonth: today.getUTCMonth() + 1,
      currentDay: today.getDate(),
      tableauVar : 260,
      indicateur: 0
    }
  }

  componentDidMount () {

    getMatchResults().then(data => {
      console.log(data)
      this.setState({
        loading: true,
        items: data,
        })
      })

    }

    //On cherche l'image du logo de la team à afficher
    renderElement(indic){
      var {loading, items, tableauVar} = this.state;

      if(!loading)
      {
        return logo = null;
      }

      else{
        let logo;
        //On parcourt notre list d'image de logo
        for(var i; imagesList.length; i++)
        {
          //Si le nom de l'équipe correspond à un nom d'image de logo
        if(items.games[260 + indic].home.name == imagesList[i].name)
        {
          //Logo prend la valeur de l'image du logo et on retourne logo
          logo = imagesList[i].cover;
          return logo;
        }
        else{
          return logo = null;
        }
      }
        console.log(arrayItem);
      }
    }


    render() {
      var {loading, items, tableauVar} = this.state;

      if(!loading)
      {
        return <div>Loading...</div>;
      }
      else{

        return (
          <Table striped bordered hover>
            <thead>
              <tr>
                <th>Logo Home Team</th>
                <th>Home Team</th>
                <th>Score</th>
                <th>Away Team</th>
                <th>Logo Away Team</th>
              </tr>
            </thead>
            {items.games.slice(260, (tableauVar+5)).map(item => (
            <tbody key={item.id}>
              <tr>
                <td>LOGO{this.renderElement(indicateur)}</td>
                <td>{item.home.name}</td>
                <td>{item.home_points} - {item.away_points}</td>
                <td>{item.away.name}</td>
                <td>2</td>
              </tr>
              {this.setState({indicateur: indicateur + 1})}
            </tbody>
            ))}
          </Table>
        );
      }

      }
    
  }
