import React, { Component } from 'react';
import { Carousel, ToastBody } from 'react-bootstrap';
import '../cssFiles/home.css';
import {getMatchResults} from '../API/API_MatchResults'

export default class MatchResults extends Component {
  //Constructeur : intialisation de chaque paramètre du state utilisés
  constructor(props) {
    super(props);
    var today = new Date();
    this.state = {
      loading: false,
      items: null,
      currentYear: today.getUTCFullYear(),
      currentMonth: today.getUTCMonth() + 1,
      currentDay: today.getDate()
    }
  }

  componentDidMount () {

    getMatchResults().then(data => {
      console.log(data)
      this.setState({
        loading: true,
        items: data,
        })
      })

    }

    render() {
      var {loading, items} = this.state;

      if(!loading)
      {
        return <div>Loading...</div>;
      }
      else{

        return (
          <div>
            Has been loaded.
            Year : {this.state.currentYear}
            Month : {this.state.currentMonth}
            Day : {this.state.currentDay}
          </div>
        );
      }

      }
    
  }
