import React from 'react';
import {getRanking} from '../API/API_Ranking.js'




class Ranking extends React.Component {

    //Constructeur : intialisation de chaque paramètre du state utilisés
      constructor(props) {
          super(props)
          this.state = {
            teamsWestern: [],
            teamsEastern: []
          }
        }

        //Mise à jour du state à la construction du component, avec les données collectés de l'api donnant la liste des joueurs
        componentDidMount()
        {
            this._loadTeams()
        }

        _loadTeams() {
          getRanking().then(data => {
            //console.log(data.conferences[1])
            this.setState({
              teamsEastern: [ ...this.state.teamsWestern, ...data.conferences[0].divisions[0].teams, ...data.conferences[0].divisions[1].teams, ...data.conferences[0].divisions[2].teams],
              teamsWestern: [...this.state.teamsEastern, ...data.conferences[1].divisions[0].teams, ...data.conferences[1].divisions[1].teams, ...data.conferences[1].divisions[2].teams]
            })
            console.log(this.state.teamsEastern)
            console.log(this.state.teamsWestern)


          })
        }


      render() {
          return (
            <div>
                <h2>ATP Ranking</h2>
                <p>Coin coin je t'aime</p>
            </div>
          )
      }
  }

  export default Ranking
